void _processDo(int option) throws IOException {
    if (debugoptions) {
        System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
    }

    if (__notifhand != null) {
        __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
    }

    boolean acceptNewState = false;


    /* open TelnetOptionHandler functionality (start)*/
    if (optionHandlers[option] != null) {
        acceptNewState = optionHandlers[option].getAcceptLocal();
    } else {
    /* open TelnetOptionHandler functionality (end)*/
        /* TERMINAL-TYPE option (start)*/
        if (option == TERMINAL_TYPE) {
            if ((terminalType != null) && (terminalType.length() > 0)) {
                acceptNewState = true;
            }
        }
        /* TERMINAL-TYPE option (end)*/
    /* open TelnetOptionHandler functionality (start)*/
    }
    /* open TelnetOptionHandler functionality (end)*/

    if (_willResponse[option] > 0) {
        -- _willResponse[option];
        if (_willResponse[option] > 0 && _stateIsWill(option)) {
            -- _willResponse[option];
        }
    }

    if (_willResponse[option] == 0) {
        if (_requestedWont(option)) {
            switch (option) {
            default:
                break;
            }

            if (acceptNewState) {
                _setWantWill(option);
                _sendWill(option);
            } else {
                ++_willResponse[option];
                _sendWont(option);
            }
        } else {
            // Other end has acknowledged option.
            switch (option) {
            default:
                break;
            }
        }
    }

    _setWill(option);
}

    public void run() {
        try {
            while(!stopped) {
                try {
                    select();
                    processAcceptedConnections();
                    processInterestOpsUpdateRequests();
                } catch (RuntimeException e) {
                    LOG.warn("Ignoring unexpected runtime exception", e);
                }
catch (Exception e) {
                    LOG.warn("Ignoring unexpected exception", e);
                }
            }

                // Close connections still pending on the selector. Any others
                // with in-flight work, let drain out of the work queue.

            for (SelectionKey key : selector.keys()) {
                NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
                if (cnxn.isSelectable()) {
                    cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
                }
                cleanupSelectionKey(key);
            }

            SocketChannel accepted;
            while((accepted = acceptedQueue.poll()) != null) {
                fastCloseSock(accepted);
            }
            updateQueue.clear();
        }
        finally {
                    closeSelector();
                // This will wake up the accept thread and the other selector
                // threads, and tell the worker thread pool to begin shutdown.
                    NIOServerCnxnFactory.this.stop();
                    LOG.info("selector thread exited run method");
        }
    }

private static Object process(final RedisInputStream is) {

    final byte b = is.readByte();
// System.out.println("BYTE: " + (char) b);
    switch (b) {
        case PLUS_BYTE:
            return is.readLineBytes();
        case DOLLAR_BYTE: case EQUAL_BYTE:
            return processBulkReply(is);
        case ASTERISK_BYTE:
            return processMultiBulkReply(is);
        case UNDERSCORE_BYTE:
            return is.readNullCrLf();
        case HASH_BYTE:
            return is.readBooleanCrLf();
        case COLON_BYTE:
            return is.readLongCrLf();
        case COMMA_BYTE:
            return is.readDoubleCrLf();
        case LEFT_BRACE_BYTE:
            return is.readBigIntegerCrLf();
        case PERCENT_BYTE: // TODO: currently just to start working with HELLO
            return processMapKeyValueReply(is);
        case TILDE_BYTE: // TODO:
            return processMultiBulkReply(is);
        case GREATER_THAN_BYTE:
            return processMultiBulkReply(is);
        case MINUS_BYTE:
            processError(is);
            return null;
  // TODO: Blob error '!'
        default:
            throw new JedisConnectionException("Unknown reply: " + (char) b);
    }
}

void drainFused() {
int missed = 1;
for (;;) {

    if (disposed) {
             return;
    }

    boolean d = done;
    Throwable ex = error;
    if (!delayError && d && ex != null) {
             disposed = true;
             downstream.onError(error);
             worker.dispose();
             return;
    }

    downstream.onNext(null);

    if (d) {
             disposed = true;
             ex = error;

             if (ex != null) {
               downstream.onError(ex);
             } else {
               downstream.onComplete();
             }

             worker.dispose();
             return;
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

    private void flushAuxiliar(long amount) throws IOException {
        if (aux.length < 1) {
            return;
        }
        out.flush();
        aux.flush();
        boolean underflow = aux.offset < aux.length || out.offset < out.length;
        byte[] buffer = new
byte[COPY_BUFFER_SIZE];
        aux.target.seek(0);
        out.target.seek(out.length);
        long length = amount;
        while (length > 0) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
            if (read < 1) {
                amount -= length;
                break;
            }
            out.writeProof(buffer, read);
            length -= read;
        }

        if (underflow) {
            if (out.offset >= out.length) {
                // calculate the aux underflow pointer
                if (aux.offset < amount) {
                    out.offset += aux.offset;
                    aux.offset = 0;
                    out.target.seek(out.offset);
                    } else {
                    aux.offset -= amount;
                    out.offset = out.length+ amount;
                }
                } else {
                aux.offset = 0;
            }
        } else {
            out.offset += amount;
            aux.offset -= amount;
        }
        out.length += amount;
        if (out.length > maxLengthKnown) {
            maxLengthKnown = out.length;
        }

        if (amount < aux.length) {
            // move the excess data to the beginning of the file
            long readOffset = amount;
            long writeOffset = 0;
            aux.length -= amount;
            length = aux.length;
            while (length > 0) {
                int read = (int) Math.min(length, Integer.MAX_VALUE);
                read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
                aux.target.seek(writeOffset);
                aux.writeProof(buffer, read);
                writeOffset += read;
                readOffset += read;
                length -= read;
                aux.target.seek(readOffset);
            }
            aux.target.setLength(aux.length);
            return;
        }

        if (aux.length > THRESHOLD_AUX_LENGTH) {
            aux.target.setLength(THRESHOLD_AUX_LENGTH);// or setLength(0);
        }
        aux.reset();
    }

private static void handleError(final Context context, final ErrorInfo errorInfo) {
    if (errorInfo.getThrowable() != null) {
        errorInfo.getThrowable()
        .printStackTrace();
    }

    if (errorInfo.getThrowable() instanceof ReCaptchaException) {
        Toast.makeText(context, R.string.recaptcha_request_toast, Toast.LENGTH_LONG)
        .show();
        // Starting ReCaptcha Challenge Activity

        final Intent intent = new Intent(context, ReCaptchaActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        context.startActivity(intent);
    } else if (errorInfo.getThrowable() != null && ExceptionUtils.isNetworkRelated(errorInfo.getThrowable())) {
        Toast.makeText(context, R.string.network_error, Toast.LENGTH_LONG)
        .show();
    } else if (errorInfo.getThrowable() instanceof AgeRestrictedContentException) {
               Toast.makeText(context, R.string.restricted_video_no_stream, Toast.LENGTH_LONG)
               .show();
           } else if (errorInfo.getThrowable() instanceof GeographicRestrictionException) {
               Toast.makeText(context, R.string.georestricted_content, Toast.LENGTH_LONG)
               .show();
           } else if (errorInfo.getThrowable() instanceof PaidContentException) {
                      Toast.makeText(context, R.string.paid_content, Toast.LENGTH_LONG)
                      .show();
                  } else if (errorInfo.getThrowable() instanceof PrivateContentException) {
                      Toast.makeText(context, R.string.private_content, Toast.LENGTH_LONG)
                      .show();
                  } else if (errorInfo.getThrowable() instanceof SoundCloudGoPlusContentException) {
                             Toast.makeText(context, R.string.soundcloud_go_plus_content, Toast.LENGTH_LONG)
                             .show();
                         } else if (errorInfo.getThrowable() instanceof YoutubeMusicPremiumContentException) {
                             Toast.makeText(context, R.string.youtube_music_premium_content, Toast.LENGTH_LONG)
                             .show();
                         } else if (errorInfo.getThrowable() instanceof ContentNotAvailableException) {
                                    Toast.makeText(context, R.string.content_not_available, Toast.LENGTH_LONG)
