                                    ErrorUtil.createNotification(context, errorInfo);
                                }

    if (context instanceof RouterActivity) {
        ((RouterActivity) context)
        .finish();
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<?
                super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;
    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }
            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;

            if (d
                    && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }
            if (empty
                || current.item == null) {
                break;
            }
            inner.compareAndSet(current, null);
            downstream.onNext(current.item);
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

private void bind(Configure configure) throws Throwable {
    long start = System.currentTimeMillis();
    if (!isBindRef.compareAndSet(false, true)) {
        throw

        new IllegalStateException("already bind");
    }

    // init random port

    if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
        int newTelnetPort = SocketUtils.findAvailableTcpPort();

        configure.setTelnetPort(newTelnetPort);
        logger().info("generate random telnet port: " + newTelnetPort);
    }

    if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
        int newHttpPort = SocketUtils.findAvailableTcpPort();

        configure.setHttpPort(newHttpPort);
        logger().info("generate random http port: " + newHttpPort);
    }
    // try to find appName

    if (configure.getAppName() == null) {
        configure.setAppName(System.getProperty(ArthasConstants.PROJECT_NAME, System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
    }

    try {
        if (configure.getTunnelServer() != null) {
            tunnelClient = new TunnelClient();
            tunnelClient.setAppName(configure.getAppName());
            tunnelClient.setId(configure.getAgentId());
            tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
            tunnelClient.setVersion(ArthasBanner.version());
            ChannelFuture channelFuture = tunnelClient.start();

            channelFuture.await(10, TimeUnit.SECONDS);
        }
    } catch (Throwable t) {
        logger().error("start tunnel client error", t);
    }

    try {
        ShellServerOptions options = new ShellServerOptions().setInstrumentation(instrumentation).setPid(PidUtils.currentLongPid()).setWelcomeMessage(ArthasBanner.welcome());
        if (configure.getSessionTimeout() != null) {
            options.setSessionTimeout(configure.getSessionTimeout()*1000);
        }

        this.httpSessionManager = new HttpSessionManager();
        if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
            //  listen 0.0.0.0 
            String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! " + "No password is currently configured. " + "Therefore, a default password is generated, " + "and clients need to use the password to connect!";

            AnsiLog.error(errorMsg);
            configure.setPassword(StringUtils.randomString(64));
            AnsiLog.error("Generated arthas password: " + configure.getPassword());
            logger().error(errorMsg);
            logger().info("Generated arthas password: " + configure.getPassword());
        }

        this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());
        shellServer = new ShellServerImpl(options);
        List<String> disabledCommands = new ArrayList<String>();
        if (configure.getDisabledCommands() != null) {
            String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
            if (strings != null) {
                disabledCommands.addAll(Arrays.asList(strings));
            }
        }

        BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
        List<CommandResolver> resolvers = new ArrayList<CommandResolver>();

        resolvers.add(builtinCommands);

        //worker group
        workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));

        // TODO: discover user provided command resolver
        if (configure.getTelnetPort() != null && configure.getTelnetPort() > 0) {
            logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
            shellServer.registerTermServer(new HttpTelnetTermServer(configure.getIp(), configure.getTelnetPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
        } else {
            logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
        }

        if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
            logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
            shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
        } else {
            // listen local address in VM communication
            if (configure.getTunnelServer() != null) {
                shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
            }

            logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
        }

        for (CommandResolver resolver : resolvers) {
            shellServer.registerCommandResolver(resolver);
        }

        shellServer.listen(new BindHandler(isBindRef));
        if (!isBind()) {
            throw

            new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: " + String.valueOf(configure.getTelnetPort())+ ", http port: " + String.valueOf(configure.getHttpPort()));
        }

        //http api session manager

        sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());
        //http api handler
        httpApiHandler = new HttpApiHandler(historyManager, sessionManager);
        logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(), configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());

        // 
        if (configure.getStatUrl() != null) {
            logger().info("arthas stat url: {}", configure.getStatUrl());
        }

        UserStatUtil.setStatUrl(configure.getStatUrl());
        UserStatUtil.setAgentId(configure.getAgentId());
        UserStatUtil.arthasStart();
        try {
            SpyAPI.init();
        } catch (Throwable e) {
            // ignore
        }

        logger().info("as-server started in {} ms", System.currentTimeMillis() - start);
    } catch (Throwable e) {
        logger().error("Error during start as-server", e);
        destroy();
        throw e;
    }
}

@Bean(name = "machineType" )
public String determineMachineType() {
    try {
        boolean isDocker = runningInDocker();
        boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;
        boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));
        if (isKubernetes) {
            return "Kubernetes";
        } else if (isDocker) {
                   return "Docker";
        } else if (isBrowserOpen) {
                   String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);
                   if (os.contains("win")) {
                       return "Client-windows";
                   } else if (os.contains("mac")) {
                              return "Client-mac";
                   } else {
                              return "Client-unix";
                   }
               } else {
                   return "Server-jar";
               }
    } catch (Exception e) {
        return "Unknown";
    }
}

synchronized
public static ZooKeeper getClient(String contextPath,
                                  String session,
                                  int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);
    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new " + "connection for : '%s'", connectionId));
        }

        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));
        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }
        zkMap.put(connectionId, zk);

       // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }

    return zk;
}

@SuppressWarnings("unchecked" ) void remove(PublishSubscription<T> ps) {
for (;;) {
                                                                           PublishSubscription<T>[] a = subscribers.get();
                                                                           if (a == TERMINATED || a == EMPTY) {
             return;
                                                                           }

                                                                           int n = a.length;
                                                                           int j = -1;
                                                                           for (int i = 0; i < n; i++) {

             if (a[i] == ps) {
                                                                                                           j = i;
                                                                                                           break;
             }
                                                                           }

                                                                           if (j < 0) {
             return;
                                                                           }

                                                                           PublishSubscription<T>[] b;
                                                                           if (n == 1) {
             b = EMPTY;
                                                                           } else {
                                                                             b = new PublishSubscription[n - 1];
                                                                             System.arraycopy(a, 0, b, 0, j);
                                                                             System.arraycopy(a, j + 1, b, j, n - j - 1);
                                                                           }

                                                                           if (subscribers.compareAndSet(a, b)) {
             return;
                                                                           }
}
}

    private

    int __read(boolean mayBlock) throws IOException {
        int ch;

        while (true) {

            // If there is no more data AND we were told not to block,
            // just return WOULD_BLOCK (-2). (More efficient than exception.)
            if (!mayBlock && super.available() == 0) {
                return WOULD_BLOCK;
            }

            // Otherwise, exit only when we reach end of stream.

            if ((ch = super.read())< 0) {
                return EOF;
            }

            ch = (ch& 0xff);

            /* Code Section added for supporting AYT (start)*/
            synchronized (__client) {
                                    __client._processAYTResponse();
            }
            /* Code Section added for supporting AYT (end)*/

            /* Code Section added for supporting spystreams (start)*/

            __client._spyRead(ch);
            /* Code Section added for supporting spystreams (end)*/
            switch (__receiveState) {
            case _STATE_CR:
                if (ch == '\0') {
                    // Strip null
                    continue;
                }
                // How do we handle newline after cr?
                //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&

                // Handle as normal data by falling through to _STATE_DATA case

                //$FALL-THROUGH$

            case _STATE_DATA:
                if (ch == TelnetCommand.IAC) {
                    __receiveState = _STATE_IAC;
                    continue;
                }

                if (ch == '\r') {
                    synchronized (__client) {
if (__client._requestedDont(TelnetOption.BINARY)) {
    __receiveState = _STATE_CR;
} else {
    __receiveState = _STATE_DATA;
}
                    }
                } else {
                    __receiveState = _STATE_DATA;
                }

                break;
            case _STATE_IAC:
                switch (ch) {
                case TelnetCommand.WILL:
                    __receiveState = _STATE_WILL;
                    continue;
                case TelnetCommand.WONT:
                    __receiveState = _STATE_WONT;
                    continue;
                case TelnetCommand.DO:
                    __receiveState = _STATE_DO;
                    continue;
                case TelnetCommand.DONT:
                    __receiveState = _STATE_DONT;
                    continue;
                /* TERMINAL-TYPE option (start)*/
                case TelnetCommand.SB:
                    __suboption_count = 0;
                    __receiveState = _STATE_SB;
                    continue;
                /* TERMINAL-TYPE option (end)*/
                case TelnetCommand.IAC:
                    __receiveState = _STATE_DATA;
                    break; // exit to enclosing switch to return IAC from read
                case TelnetCommand.SE: // unexpected byte! ignore it (don't send it as a command)
                    __receiveState = _STATE_DATA;
                    continue;
                default:
                    __receiveState = _STATE_DATA;
                    __client._processCommand(ch); // Notify the user
                    continue; // move on the next char
                }

                break; // exit and return from read
            case _STATE_WILL:
                synchronized (__client) {
                                        __client._processWill(ch);
                                        __client._flushOutputStream();
                }

                __receiveState = _STATE_DATA;
                continue;
            case _STATE_WONT:
                synchronized (__client) {
                                        __client._processWont(ch);
                                        __client._flushOutputStream();
                }

                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DO:
                synchronized (__client) {
                                        __client._processDo(ch);
                                        __client._flushOutputStream();
                }

                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DONT:
                synchronized (__client) {
                                        __client._processDont(ch);
                                        __client._flushOutputStream();
                }

                __receiveState = _STATE_DATA;
                continue;
            /* TERMINAL-TYPE option (start)*/
            case _STATE_SB:
                switch (ch) {
                case TelnetCommand.IAC:
                    __receiveState = _STATE_IAC_SB;
                    continue;
                default:
                    // store suboption char
                    if (__suboption_count <__suboption.length) {
                        __suboption[__suboption_count++] = ch;
                    }

                    break;
                }

                __receiveState = _STATE_SB;
                continue;
            case _STATE_IAC_SB: // IAC received during SB phase
                switch (ch) {
                case TelnetCommand.SE:
                    synchronized (__client) {
                                            __client._processSuboption(__suboption, __suboption_count);
                                            __client._flushOutputStream();
                    }

                    __receiveState = _STATE_DATA;
                    continue;
                case TelnetCommand.IAC: // De-dup the duplicated IAC
                    if (__suboption_count <__suboption.length) {
                        __suboption[__suboption_count++] = ch;
                    }

                    break;
                default:            // unexpected byte! ignore it
                    break;
                }

                __receiveState = _STATE_SB;
                continue;
            /* TERMINAL-TYPE option (end)*/
            }

            break;
        }
        return ch;
    }

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);
    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) request.append(' ').append(recovery.toString()).append(' ');
    }
    request.append("]");
    String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }
    ErrorUtil.createNotification(mContext, new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject), action, service, request.toString(), reason));
}

@Override
public Map.Entry<T, ProfilingInfo> build(Object data) {
    List list = (List) data;

    if (list == null
        || list.isEmpty()) return null;
    if (list.get(0) instanceof KeyValue) { // RESP3
        Object resultsData = null, profileData = null;
        for (KeyValue keyValue : (List<KeyValue>) data) {
            String keyStr = BuilderFactory.STRING.build(keyValue.getKey());
            switch (keyStr) {
                case PROFILE_STR_REDIS7: case PROFILE_STR_REDIS8:
                    profileData = keyValue.getValue();
                    break;
                case RESULTS_STR_REDIS7:
                    resultsData = data;
                    break;
                case RESULTS_STR_REDIS8:
                    resultsData = keyValue.getValue();
                    break;
            }
        }
        assert resultsData != null: "Could not detect Results data.";
        assert profileData != null: "Could not detect Profile data.";
        return KeyValue.of(resultsBuilder.build(resultsData), ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
    }
    return KeyValue.of(resultsBuilder.build(list.get(0)), ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

public static void openVideoDetailFragment(@NonNull final Context context, @NonNull final FragmentManager fragmentManager, final int serviceId, @Nullable final String url, @NonNull final String title, @Nullable final PlayQueue playQueue, final boolean switchingPlayers) {
    final boolean autoPlay;
    @Nullable final PlayerType playerType = PlayerHolder.getInstance().getType();

    if (playerType == null) {
        // no player open
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else if (switchingPlayers) {
        // switching player to main player
        autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
    } else if (playerType == PlayerType.MAIN) {
        // opening new stream while already playing in main player
               autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
           } else {
        // opening new stream while already playing in another player
               autoPlay = false;
           }

    final RunnableWithVideoDetailFragment onVideoDetailFragmentReady = detailFragment-> {
                                                                           expandMainPlayer(detailFragment.requireActivity());
                                                                           detailFragment.setAutoPlay(autoPlay);

                                                                           if (switchingPlayers) {
            // Situation when user switches from players to main player. All needed data is
            // here, we can start watching (assuming newQueue equals playQueue).
            // Starting directly in fullscreen if the previous player type was popup.
                                                                               detailFragment.openVideoPlayer(playerType == PlayerType.POPUP || PlayerHelper.isStartMainPlayerFullscreenEnabled(context));
                                                                           } else {
                                                                               detailFragment.selectAndLoadVideo(serviceId, url, title, playQueue);
                                                                           }

                                                                           detailFragment.scrollToTop();
                                                                       };
    final Fragment fragment = fragmentManager.findFragmentById(R.id.fragment_player_holder);

    if (fragment instanceof VideoDetailFragment && fragment.isVisible()) {
        onVideoDetailFragmentReady.run((VideoDetailFragment) fragment);
    } else {
        // Specify no url here, otherwise the VideoDetailFragment will start loading the
        // stream automatically if it's the first time it is being opened, but then
        // onVideoDetailFragmentReady will kick in and start another loading process.
        // See VideoDetailFragment.wasCleared() and its usage in doInitialLoadLogic().
        final VideoDetailFragment instance = VideoDetailFragment.getInstance(serviceId, null, title, playQueue);
        instance.setAutoPlay(autoPlay);
        defaultTransaction(fragmentManager).replace(R.id.fragment_player_holder, instance).runOnCommit(() ->
                                                                                              onVideoDetailFragmentReady.run(instance))
                                                                                              .commit();
    }
}

private static
String formatFileSize(long size) {
    String hrSize;
    double b = size;
    double k = size/1024.0;
    double m = ((size/1024.0)/1024.0);
    double g = (((size/1024.0)/1024.0)/1024.0);
    double t = ((((size/1024.0)/1024.0)/1024.0)/1024.0);
    DecimalFormat dec = new DecimalFormat("0.00");
    if (t > 1) {
        hrSize = dec.format(t).concat(" TB");
    } else if (g > 1) {
               hrSize = dec.format(g).concat(" GB");
           } else if (m > 1) {
                      hrSize = dec.format(m).concat(" MB");
                  } else if (k > 1) {
                             hrSize = dec.format(k).concat(" KB");
                         } else {
                             hrSize = dec.format(b).concat(" Bytes");
                         }
    return hrSize;
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }

    boolean b;
    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }
    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

private void processRequest(int limitPerDay,
                            String identifier,
                            Map<String, Bucket> buckets,
                            HttpServletRequest request,
                            HttpServletResponse response,
                            FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

void startMission(DownloadMission mission) {
    synchronized(this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        // create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile()) throw new RuntimeException("Cant create download metadata file");
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }
        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);
        if (mission.storage == null) {
            // noting to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) mission.errObject = new IOException("DownloadMission.storage == NULL");
            return;
        }
        boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;
        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

    private JsonNode validateLicense(String licenseKey, String machineFingerprint, LicenseContext context) throws Exception {
    String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}",
                                       licenseKey,
                                       machineFingerprint);
    HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key")).header("Content-Type", "application/vnd.api+json").header("Accept", "application/vnd.api+json")
                        // .header("Authorization", "License " + licenseKey).POST(HttpRequest.BodyPublishers.ofString(requestBody)).build();
    HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", response.body());
    JsonNode jsonResponse = objectMapper.readTree(response.body());
    if (response.statusCode() == 200) {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();
        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();
        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);

            // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
                                          context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
                                          context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);
                                          log.info("License floating (from license): {}, maxMachines: {}",
context.isFloatingLicense,
context.maxMachines);
        }

            // Also check the policy for floating license support if included

        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;
        if (includedNode.isArray()) {
                                          for (JsonNode node : includedNode) {

                                        if ("policies".equals(node.path("type").asText())) {
                                                                                 policyNode = node;
                                                                                 break;
                                        }
                                          }
        }

        if (policyNode != null) {
                // Check if this is a floating license from policy
                                          boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
                                          int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);

                // Policy takes precedence over license attributes
                                          if (policyFloating) {
                                    context.isFloatingLicense = true;
                                    context.maxMachines = policyMaxMachines;
                                          }

                                          log.info("License floating (from policy): {}, maxMachines: {}",
context.isFloatingLicense,
context.maxMachines);
        }

            // Extract user count, default to 1 if not specified

        int users = jsonResponse.path("data").path("attributes").path("metadata").path("users").asInt(1);
        applicationProperties.getPremium().setMaxUsers(users);

            // Extract isEnterprise flag
        context.isEnterpriseLicense = jsonResponse.path("data").path("attributes").path("metadata").path("isEnterprise").asBoolean(false);
        log.debug(applicationProperties.toString());
    } else {
      log.error("Error validating license. Status code: {}", response.statusCode());
    }

    return jsonResponse;
    }

private void init() throws IOException {
    File f = new File(file);
    RandomAccessFileReader in = new RandomAccessFileReader(f);
    SimpleDateFormat dateformat = new SimpleDateFormat(DATE_FORMAT);
    Pattern idp = Pattern.compile("\\[myid:(\\d+)\\]");
    long lastFp = in.getPosition();
    String line = in.readLine();
    Matcher m = null;

// if we have read data from the file, and it matches the timep pattern
    if ((line != null) && (m = timep.matcher(line)).lookingAt()) {
        starttime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. First line doesn't start with time");
    }

/*
  Count number of log entries. Any line starting with a timestamp counts as an entry
*/

    String lastentry = line;
    try {
        while(line != null) {
            m = timep.matcher(line);
            if (m.lookingAt()) {
                if (size%skipN == 0) {
                    long time = timestampFromText(dateformat, m.group(1));
                    skiplist.addMark(time, lastFp, size);
                }
                size++;
                lastentry = line;
            }

            if (serverid == 0 && (m = idp.matcher(line)).find()) {
                serverid = Integer.valueOf(m.group(1));
            }
            lastFp = in.getPosition();
            line = in.readLine();
        }
    } catch (EOFException eof) {
    // ignore, simply end of file, though really (line!=null) should have caught this


    }

    finally {
        in.close();
    }
    m = timep.matcher(lastentry);
    if (m.lookingAt()) {
        endtime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. Last line doesn't start with time");
    }
}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined, ProtectionDomain protectionDomain,byte[] classfileBuffer) throws IllegalClassFormatException {
    if (className == null) {
        return null;
    }

    className = className.replace('/', '.');
    List<RetransformEntry> allRetransformEntries = allRetransformEntries();
        // 
    ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());

    while (listIterator.hasPrevious()) {
        RetransformEntry retransformEntry = listIterator.previous();
        int id = retransformEntry.getId();
            // 
        boolean updateFlag = false;
            //  loader loader
        if (className.equals(retransformEntry.getClassName())) {
            if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
                updateFlag = isLoaderMatch(retransformEntry, loader);
            } else {
                updateFlag = true;
            }
        }

        if (updateFlag) {
            logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}", className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
            retransformEntry.incTransformCount();
            return retransformEntry.getBytes();
        }
    }
    return null;
}

@Override
public void handleResult(@NonNull final ChannelInfo result) {
    super.handleResult(result);
    currentInfo = result;
    setInitialData(result.getServiceId(), result.getOriginalUrl(), result.getName());

    if (ImageStrategy.shouldLoadImages() && !result.getBanners().isEmpty()) {
        PicassoHelper.loadBanner(result.getBanners()).tag(PICASSO_CHANNEL_TAG)
        .into(binding.channelBannerImage);
    } else {
        // do not waste space for the banner, if the user disabled images or there is not one
        binding.channelBannerImage.setImageDrawable(null);
    }

    PicassoHelper.loadAvatar(result.getAvatars()).tag(PICASSO_CHANNEL_TAG)
    .into(binding.channelAvatarView);
    PicassoHelper.loadAvatar(result.getParentChannelAvatars()).tag(PICASSO_CHANNEL_TAG)
    .into(binding.subChannelAvatarView);
    binding.channelTitleView.setText(result.getName());
    binding.channelSubscriberView.setVisibility(View.VISIBLE);

    if (result.getSubscriberCount() >= 0) {
        binding.channelSubscriberView.setText(Localization.shortSubscriberCount(activity, result.getSubscriberCount()));
    } else {
        binding.channelSubscriberView.setText(R.string.subscribers_count_not_available);
    }

    if (!TextUtils.isEmpty(currentInfo.getParentChannelName())) {
        binding.subChannelTitleView.setText(String.format(getString(R.string.channel_created_by), currentInfo.getParentChannelName()));
        binding.subChannelTitleView.setVisibility(View.VISIBLE);
        binding.subChannelAvatarView.setVisibility(View.VISIBLE);
    }

    updateRssButton();
    channelContentNotSupported = false;

    for (final Throwable throwable : result.getErrors()) {
        if (throwable instanceof ContentNotSupportedException) {
            channelContentNotSupported = true;
            showContentNotSupportedIfNeeded();
            break;
        }
    }

    disposables.clear();

    if (subscribeButtonMonitor != null) {
        subscribeButtonMonitor.dispose();
    }

    updateTabs();
    updateSubscription(result);
    monitorSubscription(result);
}

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe
@ResizeMode
public static int nextResizeModeAndSaveToPrefs(final Player player,
                                               @ResizeMode final int resizeMode) {
    final int newResizeMode;

    switch (resizeMode) {
    case AspectRatioFrameLayout.RESIZE_MODE_FIT:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_FILL:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_ZOOM: default :
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
        break;
    }

    // save the new resize mode so it can be restored in a future session

    player.getPrefs().edit().putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

public SSLContext createSslContext() throws IOException, GeneralSecurityException {
    KeyManager[] keyManagers = null;
    TrustManager[] trustManagers = null;

    if (sslVerifyMode == SslVerifyMode.FULL) {
        this.sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
    }


    else if (sslVerifyMode == SslVerifyMode.CA) {
        this.sslParameters.setEndpointIdentificationAlgorithm("");
    }


    else if (sslVerifyMode == SslVerifyMode.INSECURE) {
             trustManagers = new TrustManager[] {
                                 INSECURE_TRUST_MANAGER};
         }
    if (keystoreResource != null) {
        KeyStore keyStore = KeyStore.getInstance(keyStoreType);
        try (InputStream keystoreStream = keystoreResource.get()) {
            keyStore.load(keystoreStream, keystorePassword);
        }

        KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(keyManagerAlgorithm);
        keyManagerFactory.init(keyStore, keystorePassword);
        keyManagers = keyManagerFactory.getKeyManagers();
    }
    if (trustManagers == null && truststoreResource != null) {
        KeyStore trustStore = KeyStore.getInstance(trustStoreType);
        try (InputStream truststoreStream = truststoreResource.get()) {
            trustStore.load(truststoreStream, truststorePassword);
        }

        TrustManagerFactory trustManagerFactory = TrustManagerFactory.getInstance(trustManagerAlgorithm);
        trustManagerFactory.init(trustStore);
        trustManagers = trustManagerFactory.getTrustManagers();
    }

    SSLContext sslContext = SSLContext.getInstance(sslProtocol);
    sslContext.init(keyManagers, trustManagers, null);
    return sslContext;
}

public static void drawPlayException(TableElement table,ObjectVO throwableVO) {
    // :
    table.row("IS-RETURN", "" + false);
    table.row("IS-EXCEPTION", "" + true);

    // :
    Throwable cause;
    Throwable t = (Throwable)throwableVO.getObject();
    if (t instanceof InvocationTargetException) {
        cause = t.getCause();
    } else {
        cause = t;
    }

    if (throwableVO.needExpand()) {
        table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
    } else {
        StringWriter stringWriter = new StringWriter();
        PrintWriter printWriter = new PrintWriter(stringWriter);

        try {
            cause.printStackTrace(printWriter);
            table.row("THROW-EXCEPTION", stringWriter.toString());
        } finally {
                  printWriter.close();
        }
    }
}

    @Override int process(SharpStream out, SharpStream ... sources) throws IOException {
        // check if the subtitle is already in srt and copy, this should never happen
        String format = getArgumentAt(0, null);
        boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");
        if (format == null || format.equals("ttml")) {
            SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);
            try {
                writer.build(sources[0]);
            } catch (Exception err) {
                Log.e(TAG, "subtitle parse failed", err);
                return err instanceof IOException ?
                    1: 8;
            }
            return OK_RESULT;
        } else if (format.equals("srt")) {
                   byte[] buffer = new
byte[8*1024];
                   int read;
                   while ((read = sources[0].read(buffer)) > 0) {
                       out.write(buffer, 0, read);
                   }
                   return OK_RESULT;
                   }
        throw new UnsupportedOperationException("Can't convert this subtitle, unimplemented format: " + format);
    }

void drainLoop() {
    Observer<?
                super T> actual = this.downstream;
    int missed = 1;
    for (;;) {
        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }
            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;

            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null
                  ? q.poll(): null;
            boolean empty = v == null;

            if (d && empty
                    && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }
            if (empty) {
                break;
            }
            actual.onNext(v);
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onBroadcastReceived(final Intent intent) {
    super.onBroadcastReceived(intent);
    if (Intent.ACTION_CONFIGURATION_CHANGED.equals(intent.getAction())) {
        // Close it because when changing orientation from portrait
        // (in fullscreen mode) the size of queue layout can be larger than the screen size
        closeItemsList();
    } else if (ACTION_PLAY_PAUSE.equals(intent.getAction())) {
        // Ensure that we have audio-only stream playing when a user
        // started to play from notification's play button from outside of the app
               if (!fragmentIsVisible) {
                   onFragmentStopped();
               }
           } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_STOPPED.equals(intent.getAction())) {
                      fragmentIsVisible = false;
                      onFragmentStopped();
                  } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_RESUMED.equals(intent.getAction())) {
        // Restore video source when user returns to the fragment
                             fragmentIsVisible = true;
                             player.useVideoSource(true);

        // When a user returns from background, the system UI will always be shown even if
        // controls are invisible: hide it in that case
                             if (!isControlsVisible()) {
                                 hideSystemUIIfNeeded();
                             }
                         }
}

@POST
@Produces({MediaType.APPLICATION_JSON, "application/javascript", MediaType.APPLICATION_XML})
public Response createSession(@QueryParam("op") String op,
                              @DefaultValue("5") @QueryParam("expire") String expire,
                              @Context UriInfo ui) {
    if (!op.equals("create")) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST).entity(new ZError(ui.getRequestUri().toString(), ""))
                                                                                      .build());
    }

    int expireInSeconds;
    try {
        expireInSeconds = Integer.parseInt(expire);
    } catch (NumberFormatException e) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST).build());
    }

    String uuid = UUID.randomUUID().toString();
    while(ZooKeeperService.isConnected(contextPath, uuid)) {
        uuid = UUID.randomUUID().toString();
    }

    // establish the connection to the ZooKeeper cluster

    try {
        ZooKeeperService.getClient(contextPath, uuid, expireInSeconds);
    } catch (IOException e) {
        LOG.error("Failed while trying to create a new session", e);
        throw new WebApplicationException(Response.status(Response.Status.INTERNAL_SERVER_ERROR).build());
    }

    URI uri = ui.getAbsolutePathBuilder().path(uuid)
                                         .build();
    return Response.created(uri).entity(new JSONWithPadding(new ZSession(uuid, uri.toString())))
                                .build();
}

