@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);

    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT) || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);

    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages

    final List < Tracks.Group > textTracks = currentTracks.getGroups().stream().filter(trackGroupInfo->
                                                                                   C.TRACK_TYPE_TEXT == trackGroupInfo.getType()).collect(Collectors.toList());
    final List < String > availableLanguages = textTracks.stream().map(Tracks.Group::getMediaTrackGroup).filter(textTrack->
                                                                                                            textTrack.length > 0).map(textTrack->
                                                                                                                                     textTrack.getFormat(0).language).collect(Collectors.toList());

    // Find selected text track
    final Optional < Format > selectedTracks = textTracks.stream().filter(Tracks.Group::isSelected).filter(info->
                                                                                                       info.getMediaTrackGroup().length >= 1).map(info->
                                                                                                                                                 info.getMediaTrackGroup().getFormat(0)).findFirst();

    // Build UI
    buildCaptionMenu(availableLanguages);

    if (player.getTrackSelector().getParameters().getRendererDisabled(player.getCaptionRendererIndex()) || selectedTracks.isEmpty()) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
        binding.captionTextView.setText(selectedTracks.get().language);
    }

    binding.captionTextView.setVisibility(availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

private AsyncProfiler profilerInstance() {
    if (profiler != null) {
        return profiler;
    }

    // try to load from special path

    if (ProfilerAction.load.toString().equals(action)) {
        profiler = AsyncProfiler.getInstance(this.actionArg);
    }

    if (libPath != null) {
        // load from arthas directory
        // libattach Native Library already loaded in another classloader
        FileOutputStream tmpLibOutputStream = null;
        FileInputStream libInputStream = null;

        try {
            File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);

            tmpLibOutputStream = new FileOutputStream(tmpLibFile);
            libInputStream = new FileInputStream(libPath);
            IOUtils.copy(libInputStream, tmpLibOutputStream);
            libPath = tmpLibFile.getAbsolutePath();
            logger.debug("copy {} to {}", libPath, tmpLibFile);
        } catch (Throwable e) {
            logger.error("try to copy lib error! libPath: {}", libPath, e);
        }

        finally {
                IOUtils.close(libInputStream);
                IOUtils.close(tmpLibOutputStream);
        }

        profiler = AsyncProfiler.getInstance(libPath);
    } else {
        if (OSUtils.isLinux() || OSUtils.isMac()) {
            throw

            new IllegalStateException("Can not find libasyncProfiler so, please check the arthas directory.");
        } else {
            throw

            new IllegalStateException("Current OS do not support AsyncProfiler, Only support Linux/Mac.");
        }
    }
    return profiler;
}

@StyleRes
public static int getSettingsThemeStyle(final Context context) {
    final Resources res = context.getResources();
    final String lightTheme = res.getString(R.string.light_theme_key);
    final String blackTheme = res.getString(R.string.black_theme_key);
    final String automaticDeviceTheme = res.getString(R.string.auto_device_theme_key);
    final String selectedTheme = getSelectedThemeKey(context);

    if (selectedTheme.equals(lightTheme)) {
        return R.style.LightSettingsTheme;
    } else if (selectedTheme.equals(blackTheme)) {
               return R.style.BlackSettingsTheme;
           } else if (selectedTheme.equals(automaticDeviceTheme)) {
                      if (isDeviceDarkThemeEnabled(context)) {
            // use the dark theme variant preferred by the user
                          final String selectedNightTheme = getSelectedNightThemeKey(context);

                          if (selectedNightTheme.equals(blackTheme)) {
                              return R.style.BlackSettingsTheme;
                          } else {
                              return R.style.DarkSettingsTheme;
                          }
                      } else {
            // there is only one day theme
                          return R.style.LightSettingsTheme;
                      }
                  } else {
        // default to dark theme
                      return R.style.DarkSettingsTheme;
                  }
}

private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();
    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();
        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()



    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) continue;
        thread.interrupt();
    }
    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) continue;
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }

            if (millis > 0) thread.join(millis);
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

@Override
public TSInfo build(Object data) {
    List<KeyValue> list = (List<KeyValue>) data;
    Map<String, Object> properties = new HashMap< >();
    Map<String, String> labels = null;
    Map<String, Rule> rules = null;
    List<Map<String, Object>> chunks = null;
    for (KeyValue propertyValue : list) {
        String prop = BuilderFactory.STRING.build(propertyValue.getKey());
        Object value = propertyValue.getValue();

        if (value instanceof List) {
            switch (prop) {
                case LABELS_PROPERTY:
                    labels = BuilderFactory.STRING_MAP.build(value);
                    value = labels;
                    break;
                case RULES_PROPERTY:
                    List<KeyValue> rulesDataList = (List<KeyValue>) value;
                    Map<String, List<Object>> rulesValueMap = new HashMap< >(rulesDataList.size(), 1f);
                    rules = new HashMap< >(rulesDataList.size());
                    for (KeyValue rkv : rulesDataList) {
                        String ruleName = BuilderFactory.STRING.build(rkv.getKey());
                        List<Object> ruleValueList = BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());
                        rulesValueMap.put(ruleName, ruleValueList);
                        rules.put(ruleName, new Rule(ruleName, ruleValueList));
                    }
                    value = rulesValueMap;
                    break;
                case CHUNKS_PROPERTY:
                    List<List<KeyValue>> chunksDataList = (List<List<KeyValue>>) value;
                    List<Map<String, Object>> chunksValueList = new ArrayList< >(chunksDataList.size());
                    chunks = new ArrayList< >(chunksDataList.size());
                    for (List<KeyValue> chunkDataAsList : chunksDataList) {
                        Map<String, Object> chunk = chunkDataAsList.stream().collect(Collectors.toMap(kv -> BuilderFactory.STRING.build(kv.getKey()), kv -> BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));
                        chunksValueList.add(chunk);
                        chunks.add(chunk);
                    }
                    value = chunksValueList;
                    break;
                default:
                    value = SafeEncoder.encodeObject(value);
                    break;
            }
        }


        else if (value instanceof byte[]) {
                 value = BuilderFactory.STRING.build(value);
                 if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
                     try {
                         value = DuplicatePolicy.valueOf(((String) value).toUpperCase());
                     } catch (Exception e) {

                     }
                 }
             }
        properties.put(prop, value);
    }
    return new TSInfo(properties, labels, rules, chunks);
}

public void schedule(WorkRequest workRequest,
                     long id) {
    if (stopped) {
        workRequest.cleanup();
        return;
    }

    ScheduledWorkRequest scheduledWorkRequest = new ScheduledWorkRequest(workRequest);

    // If we have a worker thread pool, use that; otherwise, do the work
    // directly.
    int size = workers.size();
    if (size > 0) {
        try {
            // make sure to map negative ids as well to [0, size-1]
            int workerNum = ((int) (id%size) + size) %size;
            ExecutorService worker = workers.get(workerNum);
            worker.execute(scheduledWorkRequest);
        } catch (RejectedExecutionException e) {
            LOG.warn("ExecutorService rejected execution", e);
            workRequest.cleanup();
        }
    } else {
        // When there is no worker thread pool, do the work directly
        // and wait for its completion
        scheduledWorkRequest.run();
    }
}

@Override
public Bitmap transform(final Bitmap source) {
    if (DEBUG) {
        Log.d(TAG, "Thumbnail - transform() called");
    }

    final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
    final Bitmap result = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth, (int) (source.getHeight()/(source.getWidth()/notificationThumbnailWidth)), null, true);

    if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
        final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int)notificationThumbnailWidth - 1, (int) (source.getHeight()/(source.getWidth()/(notificationThumbnailWidth - 1))), null, true);
        source.recycle();
        return copied;
    } else {
        source.recycle();
        return result;
    }
}

@Override void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<?
                        super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;
    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        }


        else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone
                && isEmpty) {
                Throwable ex = error;

                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                }


                else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            }


            else if (!isEmpty) {
                     if (o instanceof WindowBoundaryRunnable) {
                         WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;

                         if (boundary.index == emitted
                             || !restartTimerOnMaxSize) {
                             this.count = 0;
                             window = createNewWindow(window);
                         }
                     }


                     else if (window != null) {
                              @SuppressWarnings("unchecked") T item = (T) o;
                              window.onNext(item);
                              long count = this.count+ 1;

                              if (count == maxSize) {
                                  this.count = 0;
                                  window = createNewWindow(window);
                              }


                              else {
                                  this.count = count;
                              }
                          }
                     continue;
                 }
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void draw(CommandProcess process, JvmModel result) {
    TableElement table = new TableElement(2, 5).leftCellPadding(1).rightCellPadding(1);
    for (Map.Entry<String, List<JvmItemVO>> entry : result.getJvmInfo().entrySet()) {
        String group = entry.getKey();
        List<JvmItemVO> items = entry.getValue();
        table.row(true, label(group).style(Decoration.bold.bold()));


        for (JvmItemVO item : items) {
            String valueStr;
            if (item.getValue() instanceof Map && item.getName().endsWith("MEMORY-USAGE")) {
                valueStr = renderMemoryUsage((Map<String, Object>)item.getValue());
            } else {
                valueStr = renderItemValue(item.getValue());
            }

            if (item.getDesc() != null) {
                table.row(item.getName() + "\n[" + item.getDesc() + "]", valueStr);
            } else {
                table.row(item.getName(), valueStr);
            }
        }
        table.row("", "");
    }
    process.write(RenderUtil.render(table, process.width()));
}

