@ SuppressLint("SwitchIntDef" ) // only fit, fill and zoom are supported by NewPipe @ResizeMode
public static int nextResizeModeAndSaveToPrefs(final Player player, @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
    case AspectRatioFrameLayout.RESIZE_MODE_FIT:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_FILL:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_ZOOM: default:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
        break;
    }

    // save the new resize mode so it can be restored in a future session
    player.getPrefs().edit().putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }

    switch (intent.getAction()) {
    case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
        pause();
        break;
    case ACTION_CLOSE:
        service.destroyPlayerAndStopService();
        break;
    case ACTION_PLAY_PAUSE:
        playPause();
        break;
    case ACTION_PLAY_PREVIOUS:
        playPrevious();
        break;
    case ACTION_PLAY_NEXT:
        playNext();
        break;
    case ACTION_FAST_REWIND:
        fastRewind();
        break;
    case ACTION_FAST_FORWARD:
        fastForward();
        break;
    case ACTION_REPEAT:
        cycleNextRepeatMode();
        break;
    case ACTION_SHUFFLE:
        toggleShuffleModeEnabled();
        break;
    case Intent.ACTION_CONFIGURATION_CHANGED:
        if (DEBUG) {
            Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
        }

        break;
    }

    UIs.call(playerUi->
        playerUi.onBroadcastReceived(intent));
}

public static String renderEnhancerAffect(EnhancerAffectVO affectVO) {
    final StringBuilder infoSB = new StringBuilder();
    List<String> classDumpFiles = affectVO.getClassDumpFiles();
    if (classDumpFiles != null) {
        for (String classDumpFile : classDumpFiles) {
            infoSB.append("[dump: ").append(classDumpFile).append("]\n");
        }
    }

    List<String> methods = affectVO.getMethods();
    if (methods != null) {
        for (String method : methods) {
            infoSB.append("[Affect method: ").append(method).append("]\n");
        }
    }
    infoSB.append(format("Affect(class count: %d , method count: %d) cost in %s ms, listenerId: %d", affectVO.getClassCount(), affectVO.getMethodCount(), affectVO.getCost(), affectVO.getListenerId()));
    if (!StringUtils.isEmpty(affectVO.getOverLimitMsg())) {
        infoSB.append("\n" + affectVO.getOverLimitMsg());
    }

    if (affectVO.getThrowable() != null) {
        infoSB.append("\nEnhance error! exception: ").append(affectVO.getThrowable());
    }
    infoSB.append("\n");
    return infoSB.toString();
}

@Override
public void close() throws IOException {
    boolean triedToClose = false, success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();
        triedToClose = true;
        super.close();
        success = true;
    }
    finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete()
                        || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file

            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;
    for(;;) {
        for(;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }
            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;
            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }
            inner.compareAndSet(current, null);
            downstream.onNext(current.item);
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

