@Override void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;
    for(;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
            } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;
            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                       if (o instanceof WindowBoundaryRunnable) {
                           WindowBoundaryRunnable boundary = (WindowBoundaryRunnable)o;
                           if (boundary.index == emitted || !restartTimerOnMaxSize) {
                               this.count = 0;
                               window = createNewWindow(window);
                           }
                           } else if (window != null) {
                                      @ SuppressWarnings("unchecked" ) T item = (T)o;
                                      window.onNext(item);
                                      long count = this.count+1;
                                      if (count == maxSize) {
                                          this.count = 0;
                                          window = createNewWindow(window);
                                      } else {
                                          this.count = count;
                                      }
                                      }
                       continue;
                       }
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

public static void addImageToDocument(PDDocument doc, PDImageXObject image, String fitOption, boolean autoRotate) throws IOException {
boolean imageIsLandscape = image.getWidth() > image.getHeight();
PDRectangle pageSize = PDRectangle.A4;
if (autoRotate && imageIsLandscape) {
    pageSize = new PDRectangle(pageSize.getHeight(), pageSize.getWidth());
}

if ("fitDocumentToImage".equals(fitOption)) {
    pageSize = new PDRectangle(image.getWidth(), image.getHeight());
}

PDPage page = new PDPage(pageSize);
doc.addPage(page);
float pageWidth = page.getMediaBox().getWidth();
float pageHeight = page.getMediaBox().getHeight();
try (PDPageContentStream contentStream = new PDPageContentStream(doc, page, AppendMode.APPEND, true, true)) {

            if ("fillPage".equals(fitOption) || "fitDocumentToImage".equals(fitOption)) {
    contentStream.drawImage(image, 0, 0, pageWidth, pageHeight);
            } else if ("maintainAspectRatio".equals(fitOption)) {
                                                                                                                float imageAspectRatio = (float) image.getWidth() / (float) image.getHeight();
                                                                                                                float pageAspectRatio = pageWidth / pageHeight;
                                                                                                                float scaleFactor = 1.0f;
                                                                                                                if (imageAspectRatio > pageAspectRatio) {
                                                                    scaleFactor = pageWidth / image.getWidth();
                                                                                                                } else {
                                                                                                                  scaleFactor = pageHeight / image.getHeight();
                                                                                                                }

                                                                                                                float xPos = (pageWidth - (image.getWidth() *scaleFactor)) / 2;
                                                                                                                float yPos = (pageHeight - (image.getHeight() *scaleFactor)) / 2;
                                                                                                                contentStream.drawImage(image, xPos, yPos, image.getWidth() *scaleFactor, image.getHeight() *scaleFactor);
                   }
} catch (IOException e) {
  log.error("Error adding image to PDF", e);
  throw e;
}
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
    lock.lock();
    try {
        entry = putIntoStore(cacheKey, entry);
        EvictionPolicy policy = getEvictionPolicy();
        policy.touch(cacheKey);
        CacheKey evictedKey = policy.evictNext();

        if (evictedKey != null) {
            delete(evictedKey);
            stats.evict();
        }
        for (Object redisKey : cacheKey.getRedisKeys()) {
            ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);

            if (redisKeysToCacheKeys.containsKey(mapKey)) {
                redisKeysToCacheKeys.get(mapKey).add(cacheKey);
            }


            else {
                Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
                set.add(cacheKey);
                redisKeysToCacheKeys.put(mapKey, set);
            }
        }
        stats.load();
        return entry;
    } finally {
        lock.unlock();
    }
}

private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }
    switch (intent.getAction()) {
    case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
        pause();
        break;
    case ACTION_CLOSE:
        service.destroyPlayerAndStopService();
        break;
    case ACTION_PLAY_PAUSE:
        playPause();
        break;
    case ACTION_PLAY_PREVIOUS:
        playPrevious();
        break;
    case ACTION_PLAY_NEXT:
        playNext();
        break;
    case ACTION_FAST_REWIND:
        fastRewind();
        break;
    case ACTION_FAST_FORWARD:
        fastForward();
        break;
    case ACTION_REPEAT:
        cycleNextRepeatMode();
        break;
    case ACTION_SHUFFLE:
        toggleShuffleModeEnabled();
        break;
    case Intent.ACTION_CONFIGURATION_CHANGED:
        if (DEBUG) {
            Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
        }
        break;
    }
    UIs.call(playerUi->
        playerUi.onBroadcastReceived(intent));
}

