@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<?
                  super R> a = downstream;
    ErrorMode em = errorMode;
    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();

                if (ex != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;
            inner = subscribers.poll();
            if (outerDone
                    && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }
            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();

            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }
                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();

                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    R v;
                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d
                            && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }
                    if (empty) {
                        break;
                    }
                    a.onNext(v);
                    e++;
                    inner.request(1L);
                }
                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }
                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();

                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d
                            && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }
        if (e != 0L
                && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }
        if (continueNextSource) {
            continue;
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;
    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;
    for(;;) {
        for(;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }
            boolean d1 = observer1.done;
            if (d1) {
                Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2 );
                    downstream.onError(e);
                    return;
                }
            }
            boolean d2 = observer2.done;
            if (d2) {
                Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2 );
                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            boolean e1 = v1 == null;
            if (v2 == null) {
                v2 = q2.poll();
            }
            boolean e2 = v2 == null;
            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }

            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2 );
                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;
                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2 );
                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2 );
                    downstream.onSuccess(false);
                    return;
                }
                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();

    notifyPostProcessing(1);
    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;

    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);
        if (err instanceof InterruptedIOException || err instanceof ClosedByInterruptException || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) errCode = ERROR_POSTPROCESSING;
        exception = err;
    }

    finally {
            notifyPostProcessing(errCode == ERROR_NOTHING? 2: 0);
    }

    if (errCode != ERROR_NOTHING) {
        if (exception == null) exception = errObject;
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }

    notifyFinished();
}

private static void handleError(final Context context, final ErrorInfo errorInfo) {
    if (errorInfo.getThrowable() != null) {
        errorInfo.getThrowable().printStackTrace();
    }

    if (errorInfo.getThrowable() instanceof ReCaptchaException) {
        Toast.makeText(context, R.string.recaptcha_request_toast, Toast.LENGTH_LONG).show();
        // Starting ReCaptcha Challenge Activity
        final Intent intent = new Intent(context, ReCaptchaActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        context.startActivity(intent);
    } else if (errorInfo.getThrowable() != null && ExceptionUtils.isNetworkRelated(errorInfo.getThrowable())) {
               Toast.makeText(context, R.string.network_error, Toast.LENGTH_LONG).show();
           } else if (errorInfo.getThrowable() instanceof AgeRestrictedContentException) {
                      Toast.makeText(context, R.string.restricted_video_no_stream, Toast.LENGTH_LONG).show();
                  } else if (errorInfo.getThrowable() instanceof GeographicRestrictionException) {
                             Toast.makeText(context, R.string.georestricted_content, Toast.LENGTH_LONG).show();
                         } else if (errorInfo.getThrowable() instanceof PaidContentException) {
                                    Toast.makeText(context, R.string.paid_content, Toast.LENGTH_LONG).show();
                                } else if (errorInfo.getThrowable() instanceof PrivateContentException) {
                                           Toast.makeText(context, R.string.private_content, Toast.LENGTH_LONG).show();
                                       } else if (errorInfo.getThrowable() instanceof SoundCloudGoPlusContentException) {
                                                  Toast.makeText(context, R.string.soundcloud_go_plus_content, Toast.LENGTH_LONG).show();
                                              } else if (errorInfo.getThrowable() instanceof YoutubeMusicPremiumContentException) {
                                                         Toast.makeText(context, R.string.youtube_music_premium_content, Toast.LENGTH_LONG).show();
                                                     } else if (errorInfo.getThrowable() instanceof ContentNotAvailableException) {
                                                                Toast.makeText(context, R.string.content_not_available, Toast.LENGTH_LONG).show();
                                                            } else if (errorInfo.getThrowable() instanceof ContentNotSupportedException) {
                                                                       Toast.makeText(context, R.string.content_not_supported, Toast.LENGTH_LONG).show();
                                                                   } else {
                                                                       ErrorUtil.createNotification(context, errorInfo);
                                                            }

    if (context instanceof RouterActivity) {
        ((RouterActivity)context).finish();
    }
}

public static String generateMachineFingerprint() {
try {
        // Get the MAC address
    StringBuilder sb = new StringBuilder();
    InetAddress ip = InetAddress.getLocalHost();
    NetworkInterface network = NetworkInterface.getByInetAddress(ip);
    if (network == null) {
    Enumeration<NetworkInterface> networks = NetworkInterface.getNetworkInterfaces();
    while (networks.hasMoreElements()) {
                             NetworkInterface net = networks.nextElement();
                             byte[] mac = net.getHardwareAddress();
                             if (mac != null) {
                                           for (int i = 0; i < mac.length; i++) {
                                                  sb.append(String.format("%02X", mac[i]));
                                           }

                                           break; // Use the first network interface with a MAC address
                             }
    }
    } else {
      byte[] mac = network.getHardwareAddress();
      if (mac != null) {
               for (int i = 0; i < mac.length; i++) {
                           sb.append(String.format("%02X", mac[i]));
               }
      }
    }

        // Hash the MAC address for privacy and consistency

    MessageDigest md = MessageDigest.getInstance("SHA-256");
    byte[] hash = md.digest(sb.toString().getBytes(StandardCharsets.UTF_8));
    StringBuilder fingerprint = new StringBuilder();
    for (byte b : hash) {
    fingerprint.append(String.format("%02x", b));
    }

    return fingerprint.toString();
} catch (Exception e) {
  return "GenericID";
}
}

public static int startArthasClient(String arthasHomeDir, List<String> telnetArgs, OutputStream out) throws Throwable {
    // start java telnet client
    // find arthas-client.jar
    URLClassLoader classLoader = new URLClassLoader(new URL[] {
                                                              new File(arthasHomeDir, "arthas-client.jar").toURI().toURL()});
    Class<?> telnetConsoleClass = classLoader.loadClass("com.taobao.arthas.client.TelnetConsole");
    Method processMethod = telnetConsoleClass.getMethod("process", String[].class);

    //redirect System.out/System.err
    PrintStream originSysOut = System.out;
    PrintStream originSysErr = System.err;
    PrintStream newOut = new PrintStream(out);
    PrintStream newErr = new PrintStream(out);

    // call TelnetConsole.process()
    // fix https://github.com/alibaba/arthas/issues/833
    ClassLoader tccl = Thread.currentThread().getContextClassLoader();
    try {
        System.setOut(newOut);
        System.setErr(newErr);
        Thread.currentThread().setContextClassLoader(classLoader);
        return (Integer)processMethod.invoke(null, new Object[] {
                                                                telnetArgs.toArray(new String[0])});
    } catch (Throwable e) {
        //java.lang.reflect.InvocationTargetException : java.net.ConnectException
        e = e.getCause();
        if (e instanceof IOException || e instanceof InterruptedException) {
            // ignore connection error and interrupted error
            return STATUS_ERROR;
        } else {
            // process error
            AnsiLog.error("process error: {}", e.toString());
            AnsiLog.error(e);
            return STATUS_EXEC_ERROR;
        }
    }
    finally {
            Thread.currentThread().setContextClassLoader(tccl);

        //reset System.out/System.err
            System.setOut(originSysOut);
            System.setErr(originSysErr);
        //flush output
            newOut.flush();
            newErr.flush();
    }
}

@Override
public Class<?> build(Object data) {
    if (data == null) return null;
    String str = STRING.build(data);
    switch (str) {
        case "null":
            return null;
        case "boolean":
            return boolean.class;
        case "integer":
            return int.class;
        case "number":
            return float.class;
        case "string":
            return String.class;
        case "object":
            return Object.class;
        case "array":
            return List.class;
        default:
            throw new JedisException("Unknown type: " + str);
    }
}

void drainLoop() {
    int missed = 1;
    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;
    for(;;) {
        long r = requested.get();
        long e = 0;
        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }
            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }
            boolean d = done.get() == 0;
            boolean empty = true;
            for(int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();
                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }
            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }
            boolean d = done.get() == 0;
            boolean empty = true;
            for(int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();

    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }

    switch (statusCode) {
    case 204:
    case 205:
    case 207:
        throw new HttpError(statusCode);
    case 416:
        return;// let the download thread handle this error
    default:
        if (statusCode < 200 || statusCode > 299) {
            throw new HttpError(statusCode);
        }
    }
}

@SuppressWarnings("unchecked") void remove(CacheDisposable<T> consumer) {
    for (;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;

        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current [i] == consumer) {
                j = i;
                break;
            }
        }
        if (j < 0) {
            return;
        }

        CacheDisposable<T>[] next;

        if (n == 1) {
            next = EMPTY;
        }


        else {
            next = new CacheDisposable[n- 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j+ 1, next, j, n - j - 1);
        }
        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

public static void openVideoDetailFragment(@NonNull final Context context, @NonNull final FragmentManager fragmentManager, final int serviceId, @Nullable final String url, @NonNull final String title, @Nullable final PlayQueue playQueue, final boolean switchingPlayers) {
    final boolean autoPlay;
    @Nullable final PlayerType playerType = PlayerHolder.getInstance().getType();
    if (playerType == null) {
        // no player open
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else if (switchingPlayers) {
        // switching player to main player
               autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
           } else if (playerType == PlayerType.MAIN) {
        // opening new stream while already playing in main player
                      autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
                  } else {
        // opening new stream while already playing in another player
                      autoPlay = false;
           }
    final RunnableWithVideoDetailFragment onVideoDetailFragmentReady = detailFragment-> {
                                                                           expandMainPlayer(detailFragment.requireActivity());
                                                                           detailFragment.setAutoPlay(autoPlay);
                                                                           if (switchingPlayers) {
            // Situation when user switches from players to main player. All needed data is
            // here, we can start watching (assuming newQueue equals playQueue).
            // Starting directly in fullscreen if the previous player type was popup.
                                                                               detailFragment.openVideoPlayer(playerType == PlayerType.POPUP || PlayerHelper.isStartMainPlayerFullscreenEnabled(context));
                                                                           } else {
                                                                                      detailFragment.selectAndLoadVideo(serviceId, url, title, playQueue);
                                                                           }
                                                                           detailFragment.scrollToTop();
                                                                       };
    final Fragment fragment = fragmentManager.findFragmentById(R.id.fragment_player_holder);
    if (fragment instanceof VideoDetailFragment && fragment.isVisible()) {
        onVideoDetailFragmentReady.run((VideoDetailFragment)fragment);
    } else {
        // Specify no url here, otherwise the VideoDetailFragment will start loading the
        // stream automatically if it's the first time it is being opened, but then
        // onVideoDetailFragmentReady will kick in and start another loading process.
        // See VideoDetailFragment.wasCleared() and its usage in doInitialLoadLogic().
        final VideoDetailFragment instance = VideoDetailFragment.getInstance(serviceId, null, title, playQueue);
        instance.setAutoPlay(autoPlay);
        defaultTransaction(fragmentManager).replace(R.id.fragment_player_holder, instance).runOnCommit(()->
                                                                                                       onVideoDetailFragmentReady.run(instance)).commit();
    }
}

public static FieldVO[] getFields(Class clazz, Integer expand) {
    Field[] fields = clazz.getDeclaredFields();
    if (fields.length == 0) {
        return new FieldVO[0];
    }

    List<FieldVO> list = new ArrayList<FieldVO>(fields.length);
    for (Field field : fields) {
        FieldVO fieldVO = new FieldVO();
        fieldVO.setName(field.getName());
        fieldVO.setType(StringUtils.classname(field.getType()));
        fieldVO.setModifier(StringUtils.modifier(field.getModifiers(), ','));
        fieldVO.setAnnotations(getAnnotations(field.getAnnotations()));
        if (Modifier.isStatic(field.getModifiers())) {
            fieldVO.setStatic(true);
            fieldVO.setValue(new ObjectVO(getFieldValue(field), expand));
        } else {
            fieldVO.setStatic(false);
        }
        list.add(fieldVO);
    }
    return list.toArray(new FieldVO[0]);
}

