void drainFused() {
    int missed = 1;
    for (;;) {
        if (disposed) {
            return;
        }

        boolean d = done;
        Throwable ex = error;

        if (!delayError && d
                && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }
        downstream.onNext(null);
        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            }


            else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@ SuppressWarnings("unchecked" ) void remove(CacheDisposable<T> consumer) {
    for(;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for(int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }
        CacheDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j+1, next, j, n - j - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

private void flushAuxiliar(long amount) throws IOException {
    if (aux.length < 1) {
        return;
    }

    out.flush();
    aux.flush();

    boolean underflow = aux.offset < aux.length || out.offset < out.length;
    byte[] buffer = new byte[COPY_BUFFER_SIZE];

    aux.target.seek(0);
    out.target.seek(out.length);

    long length = amount;

    while (length > 0) {
        int read = (int)Math.min(length, Integer.MAX_VALUE);

        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
        if (read < 1) {
            amount -= length;
            break;
        }

        out.writeProof(buffer, read);
        length -= read;
    }

    if (underflow) {
        if (out.offset >= out.length) {
            // calculate the aux underflow pointer
            if (aux.offset < amount) {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } else {
                aux.offset -= amount;
                out.offset = out.length + amount;
            }
        } else {
            aux.offset = 0;
        }
    } else {
        out.offset += amount;
        aux.offset -= amount;
    }

    out.length += amount;
    if (out.length > maxLengthKnown) {
        maxLengthKnown = out.length;
    }

    if (amount < aux.length) {
        // move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;

        aux.length -= amount;
        length = aux.length;
        while (length > 0) {
            int read = (int)Math.min(length, Integer.MAX_VALUE);

            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));
            aux.target.seek(writeOffset);
            aux.writeProof(buffer, read);
            writeOffset += read;
            readOffset += read;
            length -= read;
            aux.target.seek(readOffset);
        }

        aux.target.setLength(aux.length);
        return;
    }

    if (aux.length > THRESHOLD_AUX_LENGTH) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH);// or setLength(0);
    }

    aux.reset();
}

@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);
    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT) || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);
    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages
    final List<Tracks.Group> textTracks = currentTracks.getGroups().stream().filter(trackGroupInfo->
                                                                                    C.TRACK_TYPE_TEXT == trackGroupInfo.getType()).collect(Collectors.toList());
    final List<String> availableLanguages = textTracks.stream().map(Tracks.Group::getMediaTrackGroup).filter(textTrack->
                                                                                                             textTrack.length > 0).map(textTrack->
                                                                                                                                       textTrack.getFormat(0).language).collect(Collectors.toList());

    // Find selected text track
    final Optional<Format> selectedTracks = textTracks.stream().filter(Tracks.Group::isSelected).filter(info->
                                                                                                        info.getMediaTrackGroup().length >= 1).map(info->
                                                                                                                                                   info.getMediaTrackGroup().getFormat(0)).findFirst();

    // Build UI
    buildCaptionMenu(availableLanguages);
    if (player.getTrackSelector().getParameters().getRendererDisabled(player.getCaptionRendererIndex()) || selectedTracks.isEmpty()) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
               binding.captionTextView.setText(selectedTracks.get().language);
    }
    binding.captionTextView.setVisibility(availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

private static String extractBasicHeader(String emlContent, String headerName) {
    try {
        String[] lines = emlContent.split("\r?\n");
        for(int i = 0; i < lines.length; i++) {
            String line = lines[i];
            if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
                StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
                // Handle multi-line headers
                for(int j = i+1; j < lines.length; j++) {
                    if (lines[j].startsWith(" ") || lines[j].startsWith("\t")) {
                        value.append(" ").append(lines[j].trim());
                        } else {
                        break;
                    }
                }
                // Apply MIME header decoding
                return safeMimeDecode(value.toString());
            }
            if (line.trim().isEmpty()) break;
        }
    } catch (RuntimeException e) {
        log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
    }
    return "";
}

void drainFused() {
    int missed = 1;
    for(;;) {
        if (disposed) {
            return;
        }
        boolean d = done;
        Throwable ex = error;
        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }
        downstream.onNext(null);
        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

private static String normalizeArch(String value) {
    value = normalize(value);

    if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
        return "x86_64";
    }
    if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
        return "x86_32";
    }
    if (value.matches("^(ia64w?|itanium64)$")) {
        return "itanium_64";
    }
    if ("ia64n".equals(value)) {
        return "itanium_32";
    }
    if (value.matches("^(sparc|sparc32)$")) {
        return "sparc_32";
    }
    if (value.matches("^(sparcv9|sparc64)$")) {
        return "sparc_64";
    }
    if (value.matches("^(arm|arm32)$")) {
        return "arm_32";
    }
    if ("aarch64".equals(value)) {
        return "aarch_64";
    }
    if (value.matches("^(mips|mips32)$")) {
        return "mips_32";
    }
    if (value.matches("^(mipsel|mips32el)$")) {
        return "mipsel_32";
    }
    if ("mips64".equals(value)) {
        return "mips_64";
    }
    if ("mips64el".equals(value)) {
        return "mipsel_64";
    }
    if (value.matches("^(ppc|ppc32)$")) {
        return "ppc_32";
    }
    if (value.matches("^(ppcle|ppc32le)$")) {
        return "ppcle_32";
    }
    if ("ppc64".equals(value)) {
        return "ppc_64";
    }
    if ("ppc64le".equals(value)) {
        return "ppcle_64";
    }
    if ("s390".equals(value)) {
        return "s390_32";
    }
    if ("s390x".equals(value)) {
        return "s390_64";
    }
    return value;
}

    private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response, FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining",
stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
      long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
      response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
      response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
      response.getWriter().write("Rate limit exceeded for POST requests.");
    }
    }

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);

    request.append(mission.source);
    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) request.append(' ').append(recovery.toString()).append(' ');
    }

    request.append("]");
    String service;

    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(mContext, new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject), action, service, request.toString(), reason));
}

