        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List<EmailAttachment> attachments) throws IOException {
    if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
        return;
    }

    // 1. Find the screen position of all attachment markers
    AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
    finder.setSortByPosition(true); // Process pages in order
    finder.getText(document);
    List<MarkerPosition> markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match
    if (markerPositions.size() != attachments.size()) {
        log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
    }

    // 3. Create an invisible annotation over each found marker

    int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
    for(int i = 0; i < annotationsToAdd; i++) {
        MarkerPosition position = markerPositions.get(i);
        EmailAttachment attachment = attachments.get(i);
        if (attachment.getEmbeddedFilename() != null) {
            PDPage page = document.getPage(position.getPageIndex());
            addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
        }
    }
}

public static RequestCreator loadScaledDownThumbnail(final Context context, @NonNull final List<Image> images) {
    // scale down the notification thumbnail for performance
    return PicassoHelper.loadThumbnail(images).transform(new Transformation() {
        @Override
        public Bitmap transform(final Bitmap source) {
            if (DEBUG) {
                Log.d(TAG, "Thumbnail - transform() called");
            }
            final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
            final Bitmap result = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth, (int) (source.getHeight()/(source.getWidth()/notificationThumbnailWidth)), null, true);
            if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
                final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth - 1, (int) (source.getHeight()/(source.getWidth()/(notificationThumbnailWidth - 1))), null, true);
                source.recycle();
                return copied;
            } else {
                       source.recycle();
                       return result;
            }
        }
        @Override
        public String key() {
            return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
        }});
}

