private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();

    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();
        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) continue;
        thread.interrupt();
    }

    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) continue;
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }

            if (millis > 0) thread.join(millis);
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart == null) return false;
    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class < ?> multipartInterface = Class.forName("jakarta.mail.Multipart");

        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }

        // Additional check for MimeMultipart

        try {
            Class < ?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");

            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }

        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

    @SuppressWarnings("resource")
    public static long select(
boolean v, long telnetPortPid, String select)
    throws InputMismatchException {
        Map<Long, String> processMap = listProcessByJps(v);
        // Put the port that is already listening at the first
        if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
            String telnetPortProcess = processMap.get(telnetPortPid);
            processMap.remove(telnetPortPid);

            Map<Long, String> newProcessMap = new LinkedHashMap<Long, String>();
            newProcessMap.put(telnetPortPid, telnetPortProcess);
            newProcessMap.putAll(processMap);
            processMap = newProcessMap;
        }
        if (processMap.isEmpty()) {
            AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
            return - 1;
        }

        // select target process by the '--select' option when match only one process
        if (select != null && !select.trim().isEmpty()) {
            int matchedSelectCount = 0;
            Long matchedPid = null;
            for (Entry<Long, String> entry : processMap.entrySet()) {
                if (entry.getValue().contains(select)) {
                    matchedSelectCount++;
                    matchedPid = entry.getKey();
                }
            }
            if (matchedSelectCount == 1) {
                return matchedPid;
            }
        }

        AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
        // print list

        int count = 1;
        for (String process : processMap.values()) {
            if (count == 1) {
                System.out.println("* [" + count
+ "]: " + process);
            } else {
                System.out.println("  [" + count
+ "]: " + process);
            }

            count++;
        }

        // read choice

        String line = new Scanner(System.in).nextLine();
        if (line.trim().isEmpty()) {
            // get the first process id
            return processMap.keySet().iterator()
                                      .next();
        }

        int choice = new Scanner(line).nextInt();
        if (choice <= 0 || choice > processMap.size()) {
            return - 1;
        }

        Iterator<Long> idIter = processMap.keySet().iterator();
        for (int i = 1; i <= choice; ++ i) {
            if (i == choice) {
                return idIter.next();
            }

            idIter.next();
        }
        return - 1;
    }

