public static ZooKeeper getClient(String contextPath, String session, int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);
    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new " + "connection for : '%s'", connectionId));
        }
        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));
        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }
        zkMap.put(connectionId, zk);

       // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }
    return zk;
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List < EmailAttachment > attachments) throws IOException {
    if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
        return;
    }

    // 1. Find the screen position of all attachment markers

    AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
    finder.setSortByPosition(true); // Process pages in order
    finder.getText(document);

    List < MarkerPosition > markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match

    if (markerPositions.size() != attachments.size()) {
        log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
    }

    // 3. Create an invisible annotation over each found marker

    int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
    for (int i = 0; i < annotationsToAdd; i++) {
        MarkerPosition position = markerPositions.get(i);
        EmailAttachment attachment = attachments.get(i);

        if (attachment.getEmbeddedFilename() != null) {
            PDPage page = document.getPage(position.getPageIndex());
            addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
        }
    }
}

private boolean verifyCertificateLicense(String licenseFile, LicenseContext context) {
    try {
        String encodedPayload = licenseFile;
        // Remove the header
        encodedPayload = encodedPayload.replace(CERT_PREFIX, "");
        // Remove the footer
        encodedPayload = encodedPayload.replace(CERT_SUFFIX, "");
        // Remove all newlines
        encodedPayload = encodedPayload.replaceAll("\\r?\\n", "");

        byte[] payloadBytes = Base64.getDecoder().decode(encodedPayload);
        String payload = new String(payloadBytes);
        log.info("Decoded certificate payload: {}", payload);

        String encryptedData = "";
        String encodedSignature = "";
        String algorithm = "";
        try {
            JSONObject attrs = new JSONObject(payload);
            encryptedData = (String) attrs.get("enc");
            encodedSignature = (String) attrs.get("sig");
            algorithm = (String) attrs.get("alg");
        } catch (JSONException e) {
            log.error("Failed to parse license file: {}", e.getMessage());
            return false;
        }

        // Verify license file algorithm

        if (!"base64+ed25519".equals(algorithm)) {
            log.error("Unsupported algorithm: {}. Only base64+ed25519 is supported.", algorithm);
            return false;
        }

        // Verify signature

        boolean isSignatureValid = verifyEd25519Signature(encryptedData, encodedSignature);

        if (!isSignatureValid) {
            log.error("License file signature is invalid");
            return false;
        }

        log.info("License file signature is valid");

        // Decode the base64 data

        String decodedData;
        try {
            decodedData = new String(Base64.getDecoder().decode(encryptedData));
        } catch (IllegalArgumentException e) {
            log.error("Failed to decode license data: {}", e.getMessage());
            return false;
        }

        // Process the certificate data

        boolean isValid = processCertificateData(decodedData, context);
        return isValid;
    } catch (Exception e) {
        log.error("Error verifying certificate license: {}", e.getMessage(), e);
        return false;
    }
}

    private void bind(Configure configure)
        throws Throwable {
        long start = System.currentTimeMillis();
        if (!isBindRef.compareAndSet(false, true)) {
            throw new IllegalStateException("already bind");
        }

        // init random port
        if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
            int newTelnetPort = SocketUtils.findAvailableTcpPort();
            configure.setTelnetPort(newTelnetPort);
            logger().info("generate random telnet port: " + newTelnetPort);
        }
        if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
            int newHttpPort = SocketUtils.findAvailableTcpPort();
            configure.setHttpPort(newHttpPort);
            logger().info("generate random http port: " + newHttpPort);
        }
        // try to find appName
        if (configure.getAppName() == null) {
            configure.setAppName(System.getProperty(
                ArthasConstants.PROJECT_NAME,
                System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
        }

        try {
            if (configure.getTunnelServer() != null) {
                tunnelClient =
                    new TunnelClient();
                tunnelClient.setAppName(configure.getAppName());
                tunnelClient.setId(configure.getAgentId());
                tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
                tunnelClient.setVersion(ArthasBanner.version());

                ChannelFuture channelFuture = tunnelClient.start();
                channelFuture.await(10, TimeUnit.SECONDS);
            }
        } catch (Throwable t) {
            logger().error("start tunnel client error", t);
        }

        try {
            ShellServerOptions options = new ShellServerOptions().setInstrumentation(instrumentation)
                                                                 .setPid(PidUtils.currentLongPid())
                                                                 .setWelcomeMessage(ArthasBanner.welcome());
            if (configure.getSessionTimeout() != null) {
                options.setSessionTimeout(configure.getSessionTimeout() * 1000);
            }
            this.httpSessionManager =
                new HttpSessionManager();

            if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
                //  listen 0.0.0.0 
                String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! " + "No password is currently configured. "
+ "Therefore, a default password is generated, " + "and clients need to use the password to connect!";
                AnsiLog.error(errorMsg);
                configure.setPassword(StringUtils.randomString(64));
                AnsiLog.error("Generated arthas password: " + configure.getPassword());
                logger().error(errorMsg);
                logger().info("Generated arthas password: " + configure.getPassword());
            }
            this.securityAuthenticator =
                new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());
            shellServer =
                new ShellServerImpl(options);

            List<String> disabledCommands = new ArrayList<String>();
            if (configure.getDisabledCommands() != null) {
                String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
                if (strings != null) {
                    disabledCommands.addAll(Arrays.asList(strings));
                }
            }

            BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
            List<CommandResolver> resolvers = new ArrayList<CommandResolver>();
            resolvers.add(builtinCommands);

            //worker group
            workerGroup =
                new NioEventLoopGroup(new DefaultThreadFactory(
                                              "arthas-TermServer", true));

            // TODO: discover user provided command resolver

            if (configure.getTelnetPort() != null && configure.getTelnetPort() > 0) {
                logger().info(
                             "try to bind telnet server, host: {}, port: {}.",
                             configure.getIp(),
                             configure.getTelnetPort());
                shellServer.registerTermServer(
                    new HttpTelnetTermServer(configure.getIp(), configure.getTelnetPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
            } else {
                logger().info(
                             "telnet port is {}, skip bind telnet server.",
                             configure.getTelnetPort());
            }
            if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
                logger().info(
                             "try to bind http server, host: {}, port: {}.",
                             configure.getIp(),
                             configure.getHttpPort());
                shellServer.registerTermServer(
                    new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
            } else {
                // listen local address in VM communication
                if (configure.getTunnelServer() != null) {
                    shellServer.registerTermServer(
                        new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
                }

                logger().info(
                             "http port is {}, skip bind http server.",
                             configure.getHttpPort());
            }
            for (CommandResolver resolver : resolvers) {
                shellServer.registerCommandResolver(resolver);
            }

            shellServer.listen(
                new BindHandler(isBindRef));

            if (!isBind()) {
                throw new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: " + String.valueOf(configure.getTelnetPort())
    + ", http port: " + String.valueOf(configure.getHttpPort()));
            }

            //http api session manager

            sessionManager =
                new SessionManagerImpl(
                        options,
                        shellServer.getCommandManager(),
                        shellServer.getJobController());
            //http api handler
            httpApiHandler =
                new HttpApiHandler(historyManager, sessionManager);
            logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(), configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());

            // 

            if (configure.getStatUrl() != null) {
                logger().info("arthas stat url: {}", configure.getStatUrl());
            }

            UserStatUtil.setStatUrl(configure.getStatUrl());
            UserStatUtil.setAgentId(configure.getAgentId());
            UserStatUtil.arthasStart();
            try {
                SpyAPI.init();
            } catch (Throwable e) {
                // ignore
            }

            logger().info(
                         "as-server started in {} ms",
                         System.currentTimeMillis() - start);
        } catch (Throwable e) {
            logger().error("Error during start as-server", e);
            destroy();
            throw e;
        }
    }

private void init() throws IOException {
    File f = new File(file);
    RandomAccessFileReader in = new RandomAccessFileReader(f);
    SimpleDateFormat dateformat = new SimpleDateFormat(DATE_FORMAT);
    Pattern idp = Pattern.compile("\\[myid:(\\d+)\\]");
    long lastFp = in.getPosition();
    String line = in.readLine();
    Matcher m = null;

// if we have read data from the file, and it matches the timep pattern
    if ((line != null) && (m = timep.matcher(line)).lookingAt()) {
        starttime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. First line doesn't start with time");
    }

/*
  Count number of log entries. Any line starting with a timestamp counts as an entry
*/
    String lastentry = line;
    try {
        while (line != null) {
            m = timep.matcher(line);
            if (m.lookingAt()) {
                if (size % skipN == 0) {
                    long time = timestampFromText(dateformat, m.group(1));
                    skiplist.addMark(time, lastFp, size);
                }
                size++;
                lastentry = line;
            }
            if (serverid == 0 && (m = idp.matcher(line)).find()) {
                serverid = Integer.valueOf(m.group(1));
            }
            lastFp = in.getPosition();
            line = in.readLine();
        }
    } catch (EOFException eof) {
    // ignore, simply end of file, though really (line!=null) should have caught this

    } finally {
        in.close();
    }
    m = timep.matcher(lastentry);
    if (m.lookingAt()) {
        endtime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. Last line doesn't start with time");
    }
}

private static void sanitizeHtmlFilesInZip(Path zipFilePath, boolean disableSanitize, TempFileManager tempFileManager) throws IOException {
    try (TempDirectory tempUnzippedDir = new TempDirectory(tempFileManager)) {
        try (ZipInputStream zipIn = ZipSecurity.createHardenedInputStream(new ByteArrayInputStream(Files.readAllBytes(zipFilePath)))) {
            ZipEntry entry = zipIn.getNextEntry();
            while (entry != null) {
                Path filePath = tempUnzippedDir.getPath().resolve(sanitizeZipFilename(entry.getName()));

                if (!entry.isDirectory()) {
                    Files.createDirectories(filePath.getParent());

                    if (entry.getName().toLowerCase().endsWith(".html") || entry.getName().toLowerCase().endsWith(".htm")) {
                        String content = new String(zipIn.readAllBytes(), StandardCharsets.UTF_8);
                        String sanitizedContent = sanitizeHtmlContent(content, disableSanitize);
                        Files.write(filePath, sanitizedContent.getBytes(StandardCharsets.UTF_8));
                    } else {
                        Files.copy(zipIn, filePath);
                    }
                }

                zipIn.closeEntry();
                entry = zipIn.getNextEntry();
            }
        }

        // Repack the sanitized files

        zipDirectory(tempUnzippedDir.getPath(), zipFilePath);
    } // tempUnzippedDir auto-cleaned
}

