@PostConstruct
public void initialize() {
    log.debug("Starting resource monitoring with interval of {}ms", monitorIntervalMs);
    scheduler.scheduleAtFixedRate(
            this::updateResourceMetrics, 0, monitorIntervalMs, TimeUnit.MILLISECONDS);
}

