}

public boolean contains(File file) {
    if (file == null) {
        return false;
    }
    Path path = file.toPath();
    return registeredFiles.containsKey(path) || thirdPartyTempFiles.contains(path);
}

public void clear() {
    registeredFiles.clear();
    thirdPartyTempFiles.clear();
    tempDirectories.clear();
}

